    private void jButton3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton3MouseClicked
        // Eliminar producto
        int id = Integer.parseInt(Buscar.getText());
        daoProducto dao;
        EntidadProducto p;
        dao = new daoProducto();
        if (dao.delete(id)) {
            JOptionPane.showMessageDialog(this, "Se elimin贸 el producto exitosamente");
        } else {
            JOptionPane.showMessageDialog(this, "No se elimin贸 registro");
        }
    }//GEN-LAST:event_jButton3MouseClicked

